    ${@com.mdp.Util@trimWhere(ew.customSqlSegment)}

     and pid is null


${@com.mdp.Util@trimOrderBy(ew.customSqlSegment,'')}

${@com.mdp.Util@trimGroupBy(ew.customSqlSegment,'')}

