UPDATE XM.xm_project_phase res LEFT JOIN ( SELECT t.project_id, t.parent_phase_id, count(1) as children_cnt, sum(t.phase_act_inner_user_workload) as phase_act_inner_user_workload, sum(t.phase_act_out_user_workload) as phase_act_out_user_workload, sum(t.phase_act_workload) as phase_act_workload, sum(t.act_inner_user_at) as act_inner_user_at, sum(t.act_out_user_at) as act_out_user_at, sum(ifnull(t.act_rate,0)*ifnull(t.task_budget_workload,0)) as task_budget_workload_act_rate, sum(t.task_budget_workload) as task_budget_workload, sum(t.task_budget_at) as task_budget_at FROM xm_project_phase t WHERE t.parent_phase_id in
#{item}
GROUP BY t.project_id, t.parent_phase_id ) AS tc ON res.project_id = tc.project_id and res.id=tc.parent_phase_id SET res.phase_act_inner_user_workload = tc.phase_act_inner_user_workload, res.phase_act_out_user_workload = tc.phase_act_out_user_workload, res.phase_act_workload = tc.phase_act_workload, res.act_inner_user_at = tc.act_inner_user_at, res.act_out_user_at = tc.act_out_user_at, res.act_rate= case when tc.task_budget_workload_act_rate=0 then 0 else tc.task_budget_workload_act_rate / tc.task_budget_workload end, res.task_budget_workload=tc.task_budget_workload, res.task_budget_at=tc.task_budget_at, res.calc_time=now(), res.children_cnt=ifnull(tc.children_cnt,0) where res.id in
#{item}
and res.ntype='1'

